methods.method:
function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				var sRole = oCurObj.jxParams.children("role").text();
				var sTarget = (sRole.indexOf("slide")==-1) ? "frame" : "slide";
				var sDir = "";
				var sId;
				if(sRole=="closemodule")
				{
					oCurObj.data =
					{
						sRole: "close",
						sState: "enabled",
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
				}
				else
				{
					var bSwipe = false;
					if(sRole.indexOf("next")!=-1)
					{
						sDir = "next";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("prev")!=-1)
					{
						sDir = "prev";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("first")!=-1)
					{
						sDir = "first";
					}
					else if(sRole.indexOf("last")!=-1)
					{
						sDir = "last";
					}
					else if(sRole=="thisslide")
					{
						sDir = "this";
					}
					else
					{
						sId = oCurObj.jxParams.children((sRole=="selectedslide") ? "selectedslide" : "selectedframe").text();
					}
					oCurObj.data =
					{
						sRole: sRole,
						sTarget: sTarget,
						sDir: sDir,
						sId: sId,
						sFrameId: "",
						sSlideId: "",
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
						bNaviLockedByAction: false,
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(oCurObj.data.sRole=="close")
					{
						oCurObj.data.bDisplayConfirm = (oCurObj.jxParams.children("display_closemodule_msg").text()=="yes");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_FRAME_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_FRAME_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				}
				if(CL.bTouch)
				{
					if(bSwipe)
					{
						if(sDir=="next")
						{
							$(CL.oBoard).on("swipeleft", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}
						else if(sDir=="prev")
						{
							$(CL.oBoard).on("swiperight", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}

					}
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				else
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sTarget=="slide")
				{
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets==oCurObj.data.sDir || (argobj.sTargets=="restart" && oCurObj.data.sDir=="this") || argobj.sTargets=="all" || (argobj.sTargets=="buttons" && (oCurObj.data.sDir=="next" || oCurObj.data.sDir=="prev")) || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}					
					}
				}
				else
				{
					switch(argobj.evt)
					{
						case "EVENT_FRAME_OPENED":
						{
							if(oCurObj.data.sFrameId!=CLZ.sCurrentFrameId)
							{
								oCurObj.data.sFrameId = CLZ.sCurrentFrameId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_FRAME_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="all" || argobj.sTargets=="buttons" || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}					
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				if(argobj.evt==null) return false;
				if(oCurObj.data.sState=="disabled" || $(".cl-modal-mask").is(":visible")) return false;
				switch(argobj.evt.type)
				{
					case "swiperight":
					case "swipeleft":
					{
						oCurObj.CallMethod({ sMethod: "Navigate" });
						break;
					}
				}
				return true;
			}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jBtn = $(CLO[argobj.pid].div).find(".cl-btn");
				if(jBtn.hasClass("cl-disabled") && argobj.sState!="enabled") return false;
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "disabled":
					{
						jBtn.removeClass("cl-idle cl-over").addClass("cl-disabled");
						CLO[argobj.pid].data.sState = "disabled";
						break;
					}
					case "enabled":
					{
						jBtn.removeClass("cl-disabled").addClass("cl-idle");
						CLO[argobj.pid].data.sState = "enabled";
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
				return true;
			}

function EnableAccess(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sRole=="close")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "enabled" } });
				}
				else
				{
					var oParams = { sTarget: CLO[argobj.pid].data.sTarget };
					if(CLO[argobj.pid].data.sDir=="")
					{
						oParams.sTargetId = CLO[argobj.pid].data.sId;
					}
					else
					{
						oParams.sDir = CLO[argobj.pid].data.sDir;
					}
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: ((argobj.override==true || CL.Navigation.Allowed(oParams)) ? "enabled" : "disabled") } });
				}
				return true;
			}

function Navigate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var oParams = { sTargetType: oCurObj.data.sTarget };
				if(oCurObj.data.sDir=="")
				{
					oParams.sTargetId = oCurObj.data.sId;
				}
				else
				{
					oParams.sTargetDir = oCurObj.data.sDir;
				}
				if(argobj.override==true || CL.Navigation.Allowed(oParams))
				{
					oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
					CL.Navigation.GoTo(oParams);
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function GetProperty(argobj)
			{
				return CLO[argobj.pid].GetProperty(argobj);
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="disabled") return false;
				if(oCurObj.data.sRole=="close")
				{
					if(oCurObj.data.bDisplayConfirm)
					{
						if(confirm(CLO[argobj.pid].jxParams.children("closemodule_msg").text()))
						{
							window.close();
						}
					}
					else
					{
						window.close();
					}
				}
				else
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Navigate" });
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var bDefer = false; 
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}				
				var jBtn = jCurObjDiv.find("#" + argobj.pid + "_BUTTON_submit");
				jBtn.parent().height(jBtn.height());
				oCurObj.data =
				{
					bCreated: false,
					bCredit: (oCurObj.jxParams.children("q_scored").text()=="yes"),
					bNoMoreAttempts: false,
					bShowCorrect: (oCurObj.jxParams.children("q_showright").text()=="yes"),
					bFeedback: (oCurObj.jxParams.children("q_fb_switch").text()=="yes"),
					bModal: (oCurObj.jxParams.find("modal").text()=="yes"),
					bSkip: (oCurObj.jxParams.find("q_skip").text()!="none"),
					bTimer: (oCurObj.jxParams.children("q_timer_switch").text()=="yes"),
					bTimeout: false,
					jxObjectives: oCurObj.jxParams.find("q_scoreboards > item"),
					nBaseScore: +oCurObj.jxParams.children("q_weight").text(),
					sMode: "init",
					sType: oCurObj.jxParams.children("question_type").text(),
					sSoundSuccess: oCurObj.jxParams.children("sound_success").text(),
					sSoundFailure: oCurObj.jxParams.children("sound_failure").text(),
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				var jAudioContainer = jCurObjDiv.find(".cl-audio-files-list");
				if(oCurObj.data.sSoundClick!="none")
				{
					oCurObj.data.jAudioClick = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO1", sType: oCurObj.data.sSoundClick, sFile: oCurObj.jxParams.children("sound_click_file_custom").text() });
				}
				if(oCurObj.data.sSoundOver!="none")
				{
					oCurObj.data.jAudioOver = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO2", sType: oCurObj.data.sSoundOver, sFile: oCurObj.jxParams.children("sound_over_file_custom").text() });
				}
				if(oCurObj.data.sSoundSuccess!="none")
				{
					oCurObj.data.jAudioSuccess = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO3", sType: oCurObj.data.sSoundSuccess, sFile: oCurObj.jxParams.children("sound_success_file_custom").text() });
				}
				if(oCurObj.data.sSoundFailure!="none")
				{
					oCurObj.data.jAudioFailure = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO4", sType: oCurObj.data.sSoundFailure, sFile: oCurObj.jxParams.children("sound_failure_file_custom").text() });
				}
				switch(oCurObj.data.sType)
				{
					case "true-false":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_tf").text();
						oCurObj.data.bShuffle = (oCurObj.jxParams.children("q_elements_order").text()=="random");
						break;
					}
					case "choice":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_choice").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_choice > item");
						oCurObj.data.bShuffle = (oCurObj.jxParams.children("q_shuffle_choice").text()=="yes");
						break;
					}		
					case "select":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_select").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_select > item");
						oCurObj.data.bShuffle = (oCurObj.jxParams.children("q_shuffle_select").text()=="yes");
						break;
					}		
					case "order":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_match").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_order > item");
						oCurObj.data.bShuffle = true;
						break;
					}
					case "numeric":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_inputs").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_numeric > item");
						oCurObj.data.sOp = oCurObj.jxParams.children("q_var_logic_numeric").text();
						break;
					}
					case "fib":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_inputs").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_fib > item");
						oCurObj.data.sOp = oCurObj.jxParams.children("q_var_logic_fib").text();
						break;
					}
					case "oto":
					{
						oCurObj.data.sScoringType = oCurObj.jxParams.children("q_scoring_type_match").text();
						oCurObj.data.jxVars = oCurObj.jxParams.find("q_variants_oto > item");
						break;
					}
				}
				if(CLZ.oStore[sQuestionId]!=null)
				{
					switch(CLZ.oStore[sQuestionId].u)
					{
						case "c":
						{
							switch(oCurObj.jxParams.children("q_replay").text())
							{
								case "none_alert":
								{
									alert(oCurObj.jxParams.children("q_browse_alert").text());
									// no break
								}
								case "none_silent":
								{
									oCurObj.data.sMode = "browse";
									oCurObj.data.bNoMoreAttempts = true;
									break;
								}
								case "allow_alert":
								{
									alert(oCurObj.jxParams.children("q_secondrun_alert").text());
									// no break
								}
								case "allow_silent":
								{
									CLZ.oStore[sQuestionId] = {};
									CLZ.oStore[sQuestionId] = null;
									if(oCurObj.data.bCredit)
									{
										for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
										{
											CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
										}
									}
									break;
								}
								case "allow_confirm":
								{
									if(confirm(oCurObj.jxParams.children("q_secondrun_confirm").text()))
									{
										CLZ.oStore[sQuestionId] = {};
										CLZ.oStore[sQuestionId] = null;
										if(oCurObj.data.bCredit)
										{
											for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
											{
												CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
											}
										}
									}
									else
									{
										oCurObj.data.sMode = "browse";
										oCurObj.data.bNoMoreAttempts = true;
									}
									break;
								}
							}
							break;
						}
						case "i":
						{
							switch(oCurObj.jxParams.children("q_continue").text())
							{
								case "restart_alert":
								{
									alert(oCurObj.jxParams.children("q_restart_alert").text());
									// no break
								}
								case "restart_silent":
								{
									CLZ.oStore[sQuestionId] = {};
									CLZ.oStore[sQuestionId] = null;
									if(oCurObj.data.bCredit)
									{
										for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
										{
											CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
										}
									}
									break;
								}
								case "restore_alert":
								{
									alert(oCurObj.jxParams.children("q_restore_alert").text());
									// no break
								}
								case "restore_silent":
								{
									oCurObj.data.sMode = "continue";
									break;
								}
								case "restore_confirm":
								{
									if(confirm(oCurObj.jxParams.children("q_restore_confirm").text()))
									{
										oCurObj.data.sMode = "continue";
									}
									else
									{
										CLZ.oStore[sQuestionId] = {};
										CLZ.oStore[sQuestionId] = null;
										if(oCurObj.data.bCredit)
										{
											for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
											{
												CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
											}
										}
									}
									break;
								}
							}
							break;
						}
						default:
						{
							CLZ.oStore[sQuestionId] = {};
							CLZ.oStore[sQuestionId] = null;
							if(oCurObj.data.bCredit)
							{
								for(var j=0; j<oCurObj.data.jxObjectives.length; j++)
								{
									CL.SCO.RemoveScoreFromObjective({ sId: $(oCurObj.data.jxObjectives[j]).children("q_board").text(), sSrcId: argobj.pid });
								}
							}
							break;
						}
					}
				}
				switch(oCurObj.data.sMode)
				{
					case "init":
					{
						// create storage
						CLZ.oStore[sQuestionId] =
						{
							A:
							[
								{
									s: 0,
									d: 0,
									u: "i"
								}
							],
							O: [],
							P: [],
							R: [],
							s: (new Date()).valueOf(),
							y: CLD.CLTypeToAbbr[oCurObj.data.sType],
							u: "i"
						};
						if(oCurObj.data.bCredit)
						{
							CLZ.oStore[sQuestionId].J = [];
							oCurObj.data.jxObjectives.each(function () { CLZ.oStore[sQuestionId].J.push( $(this).children("q_board").text() ); });
						}
						if(oCurObj.data.bTimer)
						{
							var iTimer = +oCurObj.jxParams.children("q_timer").text();
							if(isNaN(iTimer))
							{
								oCurObj.data.bTimer = false;
							}
							else
							{
								CLZ.oStore[sQuestionId].t = iTimer;
								CLZ.oStore[sQuestionId].e = 0;
							}
						}
						switch(oCurObj.data.sType)
						{
							case "true-false":
							{
								if(oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="byobj" && oCurObj.jxParams.children("q_eval").text()!="byvar")
								{
									CLZ.oStore[sQuestionId].P.push( (oCurObj.jxParams.children("q_eval").text()=="true") ? 1 : 0 );
								}
								if(oCurObj.data.bShuffle)
								{
									var aShuffled = [];
									for(var i=0; i<2; i++) aShuffled[i] = CLTOOLS.Dice({ iMax: 2, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 });
									for(var i=0; i<2; i++) CLZ.oStore[sQuestionId].O.push(aShuffled[i]);
								}
								else
								{
									CLZ.oStore[sQuestionId].O = [ 0, 1 ];
								}
								// alwaus 1 attempt
								break;
							}
							case "choice":
							{
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									// 3.1.2.1
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										if($(oCurObj.data.jxVars[i-1]).children("q_right").text()=="yes")
										{
											CLZ.oStore[sQuestionId].P.push(i);
											break;
										}
									}
								}					
								if(oCurObj.data.bShuffle)
								{
									var aShuffled = [];
									for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
									for(var i=0; i<oCurObj.data.jxVars.length; i++) CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
								}
								else
								{
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										CLZ.oStore[sQuestionId].O.push(i);
									}
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType!="byvar")
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}
							case "select":
							{
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										if($(oCurObj.data.jxVars[i-1]).children("q_right").text()=="yes")
										{
											CLZ.oStore[sQuestionId].P.push(i);
										}
									}
								}					
								if(oCurObj.data.bShuffle)
								{
									var aShuffled = [];
									for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
									for(var i=0; i<oCurObj.data.jxVars.length; i++) CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
								}
								else
								{
									for(var i=1; i<=oCurObj.data.jxVars.length; i++)
									{
										CLZ.oStore[sQuestionId].O.push(i);
									}
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}
							case "order":
							{
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].P.push(i);
								}					
								var aShuffled = [];
								for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
								for(var i=0; i<oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
									CLZ.oStore[sQuestionId].R.push(aShuffled[i]+1);
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}

							case "numeric":
							case "fib":
							{
								var oPattern;
								var aConds;
								var jVar;
								var jConds;
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									jVar = $(oCurObj.data.jxVars[i-1]);
									oPattern = { sOp: jVar.children("q_cond_logic").text() };
									jConds = jVar.find("q_conditions > item");
									aConds = [];
									if(oCurObj.data.sType=="fib")
									{
										for(var j=0; j<jConds.length; j++)
										{
											aConds.push({ op: $(jConds[j]).children("q_condition").text(), val: $(jConds[j]).children("q_variant").text(), cs: ($(jConds[j]).children("q_case").text()=="yes") })
										}
									}
									else
									{
										for(var j=0; j<jConds.length; j++)
										{
											aConds.push({ op: $(jConds[j]).children("q_condition").text(), val: +$(jConds[j]).children("q_variant").text() })
										}
									}
									oPattern.aConds = aConds.slice(0);
									CLZ.oStore[sQuestionId].P.push(oPattern);
									CLZ.oStore[sQuestionId].R.push("");
								}
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].O.push(i);
								}
								var iMaxAttepmts = 1;
								if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
								{
									iMaxAttepmts = oCurObj.data.jxVars.length - 1;
								}
								else
								{
									iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}
							case "oto":
							{
								for(var i=1; i<=oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].P.push(i);
								}					
								var aShuffled = [];
								for(var i=0; i<oCurObj.data.jxVars.length; i++) aShuffled.push( CLTOOLS.Dice({ iMax: oCurObj.data.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
								for(var i=0; i<oCurObj.data.jxVars.length; i++)
								{
									CLZ.oStore[sQuestionId].O.push(aShuffled[i]+1);
									CLZ.oStore[sQuestionId].R.push(aShuffled[i]+1);
								}
								var iMaxAttepmts = 1;
								if(oCurObj.data.sScoringType=="fixed" || oCurObj.data.sScoringType=="plain" || oCurObj.data.sScoringType=="regressive" || oCurObj.data.sScoringType=="byobj") // fixed - 2.7 legacy
								{
									if(oCurObj.jxParams.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oCurObj.data.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +oCurObj.jxParams.children("q_attempts").text();
									}
								}
								for(var i=1; i<iMaxAttepmts; i++)
								{
									CLZ.oStore[sQuestionId].A.push({ u: "n" });
								}
								break;
							}

						}
						break;
					}
					case "browse":
					case "continue":
					{
						break;
					}
				}
				switch(oCurObj.data.sType)
				{
					case "true-false":
					{
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						if(oCurObj.jxParams.children("q_elements_layout").text()=="v")
						{
							for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
							{
								iIdx = +CLZ.oStore[sQuestionId].O[i];
								jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
								if(oCurObj.data.bShuffle)
								{
									jVarContainer.append( jVarDiv );
								}
								if(CLZ.oStore[sQuestionId].R.length!=0)
								{
									if(+CLZ.oStore[sQuestionId].R[0]==iIdx)
									{
										jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
									}
								}
								if(oCurObj.data.sMode=="browse")
								{
									jVarDiv.addClass("cl-var-disabled");
								}
								else
								{ 	// bind events
									if(CL.bTouch)
									{
										jVarDiv.addClass("cl-var-clickable unselectable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
									else
									{
										jVarDiv.addClass("cl-var-clickable unselectable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
								}
							}
						}
						else
						{
							var jParent = jCurObjDiv.find(".cl-q-vars-tf-h-td");
							for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
							{
								iIdx = +CLZ.oStore[sQuestionId].O[i];
								jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
								$(jParent[i]).append( jVarDiv );
								if(CLZ.oStore[sQuestionId].R.length!=0)
								{
									if(+CLZ.oStore[sQuestionId].R[0]==iIdx)
									{
										jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
									}
								}
								if(oCurObj.data.sMode=="browse")
								{
									jVarDiv.addClass("cl-var-disabled");
								}
								else
								{ 	// bind events
									if(CL.bTouch)
									{
										jVarDiv.addClass("cl-var-clickable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
									else
									{
										jVarDiv.addClass("cl-var-clickable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
									}
								}
							}
						}
						break;
					}
					case "choice":
					{
						// restoring variants area
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = +CLZ.oStore[sQuestionId].O[i];
							jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
							if(oCurObj.data.bShuffle)
							{
								jVarContainer.append( jVarDiv );
							}
							if(CLZ.oStore[sQuestionId].R.length!=0)
							{
								if(+CLZ.oStore[sQuestionId].R[0]==iIdx)
								{
									jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
								}
							}
							if(oCurObj.data.sMode=="browse")
							{
								jVarDiv.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								if(CL.bTouch)
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
								else
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
							}
						}
						if(oCurObj.data.sMode=="browse")
						{
							if(CLZ.oStore[sQuestionId].sSkip!=null)
							{
								if(CLZ.oStore[sQuestionId].sSkip=="f")
								{
									jVarContainer.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_failed").text());
								}
								else
								{
									jVarContainer.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_skip").text());
								}
							}
							else
							{
								jVarContainer.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_last").text());
							}
						}
						break;
					}
					case "select":
					{
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = +CLZ.oStore[sQuestionId].O[i];
							jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
							if(oCurObj.data.bShuffle)
							{
								jVarContainer.append( jVarDiv );
							}
							if(CLZ.oStore[sQuestionId].R.length!=0)
							{
								if($.inArray(iIdx, CLZ.oStore[sQuestionId].R)!=-1)
								{
									jVarDiv.removeClass("cl-var-idle").addClass("cl-var-selected");
								}
							}
							if(oCurObj.data.sMode=="browse")
							{
								jVarDiv.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								if(CL.bTouch)
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
								else
								{
									jVarDiv.addClass("cl-var-clickable unselectable").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								}
							}
						}
						break;
					}
					case "order":
					{
						var jVarContainer = jCurObjDiv.find(".cl-q-vars");
						var jVarDiv;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = +CLZ.oStore[sQuestionId].O[i];
							jVarDiv = $("#" + argobj.pid + "_Q_VAR_" + iIdx);
							jVarContainer.attr({ "id": argobj.pid + "_VC" }).append(jVarDiv);
							if(oCurObj.data.sMode=="browse")
							{
								jVarDiv.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								jVarDiv.addClass("unselectable cl-drag").draggable({ snap: false, scope: argobj.pid, refreshPositions: true, containment: $("#" + argobj.pid + "_VC"), axis: "y", stack: ".cl-var" }).droppable({ accept: ".cl-var", scope: argobj.pid }).on("drop", function(event, ui) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: event, elem: this, bullet: ui.draggable } }) });
							}
						}
						break;
					}
					case "numeric":
					case "fib":
					{
						var jVarDivs = jCurObjDiv.find(".cl-var");
						var jInput;
						for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
						{
							jInput = $(jVarDivs[i]).find(".cl-var-input");
							if(CLZ.oStore[sQuestionId].R[i]!="")
							{
								jInput.val(CLZ.oStore[sQuestionId].R[i]);
							}
							if(oCurObj.data.sMode=="browse")
							{
								$(jVarDivs[i]).addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								jInput.on("keyup", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("blur", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							}
						}
						break;
					}
					case "oto":
					{
						var jVarDivs = jCurObjDiv.find(".cl-var-bullet");
						oCurObj.data.jNests = [];
						jVarDivs.each(function () { oCurObj.data.jNests.push($(this).parent()); });
						var jVar;
						var iIdx;
						for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
						{
							iIdx = CLZ.oStore[sQuestionId].O[i];
							jVar = jCurObjDiv.find(".cl-var-bullet[data-vid='"+ iIdx +"']");
							jVar.appendTo(oCurObj.data.jNests[i]);
							if(oCurObj.data.sMode=="browse")
							{
								jVar.addClass("cl-var-disabled");
							}
							else
							{ 	// bind events
								jVar.addClass("unselectable cl-drag").draggable({ snap: false, refreshPositions: true, containment: jCurObjDiv.find(".cl-q-vars"), axis: "y", stack: ".cl-var-bullet" }).droppable({ accept: ".cl-var-bullet" }).on("drop", function(event, ui) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: event, elem: this, bullet: ui.draggable } }) });
							}
						}
						break;
					}
				}
				var iAttemptsLeft = 0;
				for(var i=0; i<CLZ.oStore[sQuestionId].A.length; i++)
				{
					if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
					iAttemptsLeft = CLZ.oStore[sQuestionId].A.length - i;
					break;
				}
				jCurObjDiv.find(".cl-q-attempts-num").html( iAttemptsLeft );
				if(oCurObj.data.bTimer)
				{
					oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { action: "init" } });
				}
				if(CL.bTouch)
				{
					jCurObjDiv.find(".cl-btn, .cl-q-feedback-btn").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				else
				{
					jCurObjDiv.find(".cl-btn, .cl-q-feedback-btn").on("mouseover", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				if(oCurObj.data.sMode=="init")
				{
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "hide" } });
					if(oCurObj.data.bSkip)
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "show" } });
					}
				}
				else if(oCurObj.data.sMode=="continue")
				{
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "hide" } });
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });					
				}
				else if(oCurObj.data.sMode=="browse")
				{
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "remove" } });
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });					
				}
				oCurObj.data.bCreated = true;
				if(oCurObj.data.bModal && (oCurObj.data.sMode!="browse"))
				{
					CL.Modal({ bOn: true, sSrcId: argobj.pid });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Constructor" });
						break;
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var jElem;
				if($(argobj.elem).hasClass("cl-var-spot-td"))
				{
					jElem = $(argobj.elem).parents(".cl-var:first");
				}
				else
				{
					jElem = $(argobj.elem);
				}
				if(jElem.hasClass("cl-var") || jElem.hasClass("cl-var-bullet"))
				{
					if(jElem.hasClass("cl-var-disabled")) return false;
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							// remove skip button if any
							if(!oCurObj.data.bSkipRemoved)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
								oCurObj.data.bSkipRemoved = true;
							}
							// hide feedback if any
							oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } }); 
							switch(oCurObj.data.sType)
							{
								case "true-false":
								case "choice":
								{
									if(jElem.hasClass("cl-var-selected")) return false;
									jCurObjDiv.find(".cl-var").removeClass("cl-var-selected").addClass("cl-var-idle");  // clear all selected
									jElem.removeClass("cl-var-idle").addClass("cl-var-selected");
									CLZ.oStore[sQuestionId].R[0] = +jElem.attr("data-vid");
									break;
								}
								case "select":
								{
									var iNum = +jElem.attr("data-vid"); 
									var iIdx = $.inArray(iNum, CLZ.oStore[sQuestionId].R);
									if(jElem.hasClass("cl-var-selected"))
									{
										jElem.removeClass("cl-var-selected").addClass("cl-var-idle");
										if(iIdx!=-1)
										{
											CLZ.oStore[sQuestionId].R.splice(iIdx,1);
										}
									}
									else
									{
										jElem.removeClass("cl-var-idle").addClass("cl-var-selected");
										if(iIdx==-1)
										{
											CLZ.oStore[sQuestionId].R.push(iNum);
										}
									}
									break;
								}
							}
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: ((CLZ.oStore[sQuestionId].R.length>0) ? "show" : "hide") } });
							break;
						}
						case "drop":
						{ // order
							argobj.evt.stopImmediatePropagation();
							var iTargetIdx = +jElem.attr("data-vid");
							var iBulletIdx = +$(argobj.bullet).attr("data-vid");
							var aNewOrder = [];
							if(!oCurObj.data.bSkipRemoved)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
								oCurObj.data.bSkipRemoved = true;
							}
							for(var i=0; i<CLZ.oStore[sQuestionId].O.length; i++)
							{
								if(CLZ.oStore[sQuestionId].O[i]==iTargetIdx)
								{
									aNewOrder.push(iBulletIdx);
								}
								else if(CLZ.oStore[sQuestionId].O[i]==iBulletIdx)
								{
									aNewOrder.push(iTargetIdx);
								}
								else
								{
									aNewOrder.push(CLZ.oStore[sQuestionId].O[i]);
								}
							}
							if(oCurObj.data.sType=="order")
							{
								var jParent = jElem.parent();
								CLZ.oStore[sQuestionId].O = [];
								CLZ.oStore[sQuestionId].R = [];
								for(var i=0; i<aNewOrder.length; i++)
								{
									$("#" + argobj.pid + "_Q_VAR_" + aNewOrder[i]).css({ "top": "", "z-index": "" }).appendTo(jParent);
									CLZ.oStore[sQuestionId].O.push(aNewOrder[i]);
									CLZ.oStore[sQuestionId].R.push(aNewOrder[i]);
								}
							}
							else if(oCurObj.data.sType=="oto")
							{
								CLZ.oStore[sQuestionId].O = [];
								CLZ.oStore[sQuestionId].R = [];
								for(var i=0; i<aNewOrder.length; i++)
								{
									jCurObjDiv.find(".cl-var-bullet[data-vid='"+ aNewOrder[i] +"']").css({ "top": "", "z-index": "" }).appendTo(oCurObj.data.jNests[i]);
									CLZ.oStore[sQuestionId].O.push(aNewOrder[i]);
									CLZ.oStore[sQuestionId].R.push(aNewOrder[i]);
								}
							}
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "show" } });
							break;
						}
					}
				}
				else if(jElem.hasClass("cl-btn"))
				{
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							if(jElem.attr("data-action")=="submit")
							{
								oCurObj.CallMethod({ sMethod: "Submit" });
							}
							else if(jElem.attr("data-action")=="skip")
							{
								oCurObj.CallMethod({ sMethod: "Skip" });
							}
							jElem.hide();
							break;
						}
					}
				}
				else if(jElem.hasClass("cl-var-input"))
				{
					var iIdx = +jElem.attr("data-vid") - 1;
					switch(argobj.evt.type)
					{
						case "blur":
						{
							CLZ.oStore[sQuestionId].R[iIdx] = jElem.val();
							break;
						}
						case "keyup":
						{
							var sValue = jElem.val();
							if(oCurObj.data.sType=="numeric")
							{
								sValue = CL.Q.FixNumeric({ sText: sValue });
							}
							jElem.val(sValue);
							var iIdx = +jElem.attr("data-vid") - 1;
							CLZ.oStore[sQuestionId].R[iIdx] = sValue;
							break;
						}
					}
					// remove skip button if any
					if(!oCurObj.data.bSkipRemoved)
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "skip", action: "remove" } });
						oCurObj.data.bSkipRemoved = true;
					}
					oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { target: "submit", action: "show" } });
				}
				else if(jElem.hasClass("cl-q-feedback-btn"))
				{
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } });
							break;
						}
					}
				}
				return true;	
			}

function Skip(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var sMsg = "";
				var bFail = false;
				switch(oCurObj.jxParams.children("q_skip").text())
				{
					case "skip":
					{
						sMsg = oCurObj.jxParams.children("q_goal_skip").text();
						CLZ.oStore[sQuestionId].sSkip = "s";
						break;
					}
					case "fail_confirm":
					{
						if(!confirm(oCurObj.jxParams.children("q_fail_confirm").text()))
						{
							return false;
						}
					} // break omitted intentionally
					case "fail_silent":
					{
						bFail = true;
						sMsg = oCurObj.jxParams.children("q_goal_failed").text();
						CLZ.oStore[sQuestionId].sSkip = "f";
						break;
					}
				}
				jCurObjDiv.find(".cl-q-question-goal").html(sMsg);
				oCurObj.FireEvent({ sName: "onSkipQuestion" })
				oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: { bFail: bFail, bSkip: true } });
				return true;
			}

function Submit(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid; 
				oCurObj.FireEvent({ sName: "onAttempt" });
				var bCorrect = false;
				var iCurrentTime = (new Date()).valueOf();
				var iAttemptIdx = -1;
				for(var i=0; i<CLZ.oStore[sQuestionId].A.length; i++)
				{
					if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
					iAttemptIdx = i;
					break;
				}
				if(iAttemptIdx==-1) return false; // extra fuse
				if(CLZ.oStore[sQuestionId].R.length==0)
				{
					if(argobj.sReason=="timeout")
					{
						oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
					}
					oCurObj.CallMethod({ sMethod: "CloseAttempt" });
					oCurObj.CallMethod({ sMethod: "CloseQuestion" });
				}
				else
				{
					var nScore = 0;
					switch(oCurObj.data.sType)
					{
						case "true-false":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "plain":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length!=0)
									{
										bCorrect = (+CLZ.oStore[sQuestionId].R[0]==+CLZ.oStore[sQuestionId].P[0]);
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bShowCorrect) // display correct state
										{
											for(var i=0; i<2; i++)
											{
												if(i==+CLZ.oStore[sQuestionId].P[0])
												{
													$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled");
												}
												else
												{
													$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-selected").addClass("cl-var-idle cl-var-disabled");
												}
											}
											jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
										}
										if(oCurObj.data.bCredit)
										{
											nScore = 0;
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									break;
								}
								case "byvar":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										if(+CLZ.oStore[sQuestionId].R[0]==1)
										{
											nScore = +oCurObj.jxParams.children("q_weight_true").text();
										}
										else if(+CLZ.oStore[sQuestionId].R[0]==0)
										{
											nScore = +oCurObj.jxParams.children("q_weight_false").text();
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "choice":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // fixed - 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length!=0)
									{
										bCorrect = (+CLZ.oStore[sQuestionId].R[0]==+CLZ.oStore[sQuestionId].P[0]);
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												for(var i=1; i<=oCurObj.data.jxVars.length; i++)
												{
													if(i==+CLZ.oStore[sQuestionId].P[0])
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled");
													}
													else
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-selected").addClass("cl-var-idle cl-var-disabled");
													}
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "byvar":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[0]-1]).children("q_weight_by_connection").text();
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "select":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length==CLZ.oStore[sQuestionId].P.length)
									{
										bCorrect = true;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if($.inArray(+CLZ.oStore[sQuestionId].R[i], CLZ.oStore[sQuestionId].P)==-1)
											{
												bCorrect = false;
												break;
											}
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												for(var i=1; i<=oCurObj.data.jxVars.length; i++)
												{
													if($.inArray(i,CLZ.oStore[sQuestionId].P)!=-1)
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled");
													}
													else
													{
														$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-selected").addClass("cl-var-idle cl-var-disabled");
													}
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "sum":
								case "avg":
								case "max":
								case "min":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										var nSum = 0;
										var nMax = -99999;
										var nMin = 99999;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[i]-1]).children("q_weight_by_connection").text();
											nSum += nScore;
											if(nMax<nScore) nMax = nScore;
											if(nMin>nScore) nMin = nScore;
										}
										switch(oCurObj.data.sScoringType)
										{
											case "sum":
											{
												nScore = nSum;
												break;
											}
											case "avg":
											{
												nScore = nSum/CLZ.oStore[sQuestionId].R.length;
												break;
											}
											case "max":
											{
												nScore = nMax;
												break;
											}
											case "min":
											{
												nScore = nMin;
												break;
											}
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "order":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length==CLZ.oStore[sQuestionId].P.length)
									{
										bCorrect = true;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i])
											{
												bCorrect = false;
												break;
											}
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												var jParent = jCurObjDiv.find(".cl-q-vars");
												for(var i=1; i<=oCurObj.data.jxVars.length; i++)
												{
													$("#" + argobj.pid + "_Q_VAR_" + i).removeClass("cl-var-idle").addClass("cl-var-selected cl-var-disabled").appendTo(jParent);
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "sum":
								case "avg":
								case "max":
								case "min":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										var nSum = 0;
										var nMax = -99999;
										var nMin = 99999;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i]) continue;
											nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[i]-1]).children("q_weight_by_connection").text();
											nSum += nScore;
											if(nMax<nScore) nMax = nScore;
											if(nMin>nScore) nMin = nScore;
										}
										switch(oCurObj.data.sScoringType)
										{
											case "sum":
											{
												nScore = nSum;
												break;
											}
											case "avg":
											{
												nScore = nSum/CLZ.oStore[sQuestionId].R.length;
												break;
											}
											case "max":
											{
												nScore = nMax;
												break;
											}
											case "min":
											{
												nScore = nMin;
												break;
											}
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
						case "numeric":
						case "fib":
						{
							var aCorrect = [];
							for(var i=0; i<CLZ.oStore[sQuestionId].P.length; i++)
							{
								aCorrect.push(CL.Q.EvalSet({ sType: oCurObj.data.sType, sValue: CLZ.oStore[sQuestionId].R[i], oPattern: CLZ.oStore[sQuestionId].P[i] }));
							}
							bCorrect = (oCurObj.data.sOp=="AND");
							for(var i=0; i<aCorrect.length; i++)
							{
								if(oCurObj.data.sOp=="AND")
								{
									if(aCorrect[i]!=true)
									{
										bCorrect = false;
										break;
									}
								}
								else if(oCurObj.data.sOp=="OR")
								{
									if(aCorrect[i]==true)
									{
										bCorrect = true;
										break;
									}
								}
							}
							if(oCurObj.data.bFeedback)
							{
								oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
							}
							oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
							if(bCorrect)
							{
								if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
								{
									CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
								}
								oCurObj.FireEvent({ sName: "onSuccess" });
								if(oCurObj.data.bCredit)
								{
									switch(oCurObj.data.sScoringType)
									{
										case "fixed": // 2.7 legacy
										case "plain":
										{
											nScore = oCurObj.data.nBaseScore;
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
											break;
										}
										case "regressive":
										{
											nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
											break;
										}
										case "byobj":
										{
											for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
											{
												nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
												CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
											}
											break;
										}
									}
								}
								oCurObj.CallMethod({ sMethod: "CloseQuestion" });
							}
							else
							{
								if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
								{
									CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
								}
								oCurObj.FireEvent({ sName: "onFailure" });
								if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
								{
									if(oCurObj.data.bShowCorrect) // display correct state
									{
										var jVar;
										var sString = "";
										var oPattern;
										for(var i=0; i<CLZ.oStore[sQuestionId].P.length; i++)
										{
											oPattern = CLZ.oStore[sQuestionId].P[i];
											sString = "";
											for(var j=0; j<oPattern.aConds.length; j++)
											{
												if(j>0) sString += " " + oPattern.sOp + " ";
												sString += "(";
												switch(oPattern.aConds[j].op)
												{
													case "eq": sString += "==" + oPattern.aConds[j].val; break;
													case "ne": sString += "!=" + oPattern.aConds[j].val; break;
													case "gt": sString += ">" + oPattern.aConds[j].val; break;
													case "gte": sString += ">=" + oPattern.aConds[j].val; break;
													case "lt": sString += "<" + oPattern.aConds[j].val; break;
													case "lte": sString += "<=" + oPattern.aConds[j].val; break;
													case "cn": sString += "*" + oPattern.aConds[j].val + "*"; break;
													case "ew": sString += "*" + oPattern.aConds[j].val; break;
													case "bw": sString += oPattern.aConds[j].val + "*"; break;
												}
												sString += ")";
											}
											jVar = $("#" + argobj.pid + "_Q_VAR_" + (i+1));
											jVar.find(".cl-var-input").val(sString);
											jVar.addClass("cl-var-disabled");
										}
										jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
									}
									if(oCurObj.data.bCredit)
									{
										nScore = 0;
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
								}
							}
							break;
						}
						case "oto":
						{
							switch(oCurObj.data.sScoringType)
							{
								case "fixed": // 2.7 legacy
								case "plain":
								case "regressive":
								case "byobj":
								{
									if(CLZ.oStore[sQuestionId].R.length==CLZ.oStore[sQuestionId].P.length)
									{
										bCorrect = true;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i])
											{
												bCorrect = false;
												break;
											}
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: !bCorrect, bCorrect: bCorrect } });
									if(oCurObj.data.bFeedback)
									{
										oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { correct: bCorrect } });
									}
									if(bCorrect)
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
										}
										oCurObj.FireEvent({ sName: "onSuccess" });
										if(oCurObj.data.bCredit)
										{
											switch(oCurObj.data.sScoringType)
											{
												case "fixed": // 2.7 legacy
												case "plain":
												{
													nScore = oCurObj.data.nBaseScore;
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "regressive":
												{
													nScore = oCurObj.data.nBaseScore*(CLZ.oStore[sQuestionId].A.length - iAttemptIdx);
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
												case "byobj":
												{
													for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
													{
														nScore = +$(oCurObj.data.jxObjectives[i]).children("q_weight_byobj").text();
														CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									}
									else
									{
										if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
										{
											CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
										}
										oCurObj.FireEvent({ sName: "onFailure" });
										if(oCurObj.data.bNoMoreAttempts || oCurObj.data.bTimeout)
										{
											if(oCurObj.data.bShowCorrect) // display correct state
											{
												var jParent = jCurObjDiv.find(".cl-q-vars");
												for(var i=0; i<CLZ.oStore[sQuestionId].P.length; i++)
												{
													jCurObjDiv.find(".cl-var-bullet[data-vid='"+ CLZ.oStore[sQuestionId].P[i] +"']").css({ "top": "", "z-index": "" }).appendTo(oCurObj.data.jNests[i]);
												}
												jCurObjDiv.find(".cl-q-question-goal").html(oCurObj.jxParams.children("q_goal_right").text());
											}
											if(oCurObj.data.bCredit)
											{
												nScore = 0;
												for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
												}
											}
											oCurObj.CallMethod({ sMethod: "CloseQuestion" });
										}
									}
									break;
								}
								case "sum":
								case "avg":
								case "max":
								case "min":
								{
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { bInitNext: false, bCorrect: true } });
									if(oCurObj.data.bCredit)
									{
										var nSum = 0;
										var nMax = -99999;
										var nMin = 99999;
										for(var i=0; i<CLZ.oStore[sQuestionId].R.length; i++)
										{
											if(+CLZ.oStore[sQuestionId].R[i] != +CLZ.oStore[sQuestionId].P[i]) continue;
											nScore = +$(oCurObj.data.jxVars[+CLZ.oStore[sQuestionId].R[i]-1]).children("q_weight_by_connection").text();
											nSum += nScore;
											if(nMax<nScore) nMax = nScore;
											if(nMin>nScore) nMin = nScore;
										}
										switch(oCurObj.data.sScoringType)
										{
											case "sum":
											{
												nScore = nSum;
												break;
											}
											case "avg":
											{
												nScore = nSum/CLZ.oStore[sQuestionId].R.length;
												break;
											}
											case "max":
											{
												nScore = nMax;
												break;
											}
											case "min":
											{
												nScore = nMin;
												break;
											}
										}
										for(var i=0; i<oCurObj.data.jxObjectives.length; i++)
										{
											CL.SCO.SetObjectiveScore({ sId: $(oCurObj.data.jxObjectives[i]).children("q_board").text(), sSrcId: sQuestionId, bReplace: true, nScore: nScore });
										}
									}
									oCurObj.CallMethod({ sMethod: "CloseQuestion" });
									break;
								}
							}
							break;
						}
					}
				}
				return true;
			}

function Feedback(argobj)
			{
				var oCurObj = CLO[argobj.pid]; 
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj.jxParams.children("q_fb_switch").text()!="yes") return false;
				var jFB = jCurObjDiv.find("#" + argobj.pid + "_Q_FB");
				oCurObj.iFBTimer = 0;
				if(jFB.is(":visible")) jFB.fadeOut();
				if(argobj.hide!=null)
				{
					return true;
				}
				jFB.removeClass();
				var sFB = "";
				if(argobj.correct==true)
				{
					if(oCurObj.jxParams.children("q_display_fb_correct").text()=="yes")
					{
						sFB = "correct";
					}
				}
				else if(oCurObj.data.bTimeout)
				{
					if(oCurObj.jxParams.children("q_display_fb_timeout").text()=="yes")
					{
						sFB = "timeout";
					}
				}
				else if(oCurObj.data.bNoMoreAttempts)
				{
					if(oCurObj.jxParams.children("q_display_fb_exceed").text()=="yes")
					{
						sFB = "exceed";
					}
					else if(oCurObj.jxParams.children("q_display_fb_incorrect").text()=="yes")
					{
						sFB = "incorrect";
					}
				}
				else
				{
					if(CLZ.oStore[argobj.pid].A.length>1)
					{
						if(oCurObj.data.bLastAttempt)
						{
							if(oCurObj.jxParams.children("q_display_fb_lastattempt").text()=="yes")
							{
								sFB = "lastattempt";
							}
						}
						else
						{
							if(oCurObj.jxParams.children("q_display_fb_incorrect").text()=="yes")
							{
								sFB = "incorrect";
							}
						}
					}
					else
					{
						if(oCurObj.jxParams.children("q_display_fb_incorrect").text()=="yes")
						{
							sFB = "incorrect";
						}
					}
				}
				if(sFB!="")
				{
					jFB.find(".cl-q-feedback-txt").html( oCurObj.jxParams.children("q_fb_" + sFB).text() );
					jFB.addClass("cl-q-feedback cl-q-feedback-" + sFB).fadeIn();
					if(oCurObj.jxParams.children("q_fb_timerswitch").text()=="yes")
					{
						oCurObj.iFBTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } }); }, +oCurObj.jxParams.children("q_fb_timer").text());
					}
				}
				return true;
			}

function Button(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div); 
				var jBtn = jCurObjDiv.find("#" + argobj.pid + "_BUTTON_" + argobj.target);
				if(jBtn.length!=0)
				{
					if(argobj.action=="show")
					{
						jBtn.show();
					}
					else if(argobj.action=="hide")
					{
						jBtn.hide();
					}
					else if(argobj.action=="remove")
					{
						jBtn.remove();
					}
				}
				return true;
			}

function Timer(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				if(!oCurObj.data.bTimer) return false;
				var jIndicator = jCurObjDiv.find(".cl-q-timer-indicator");
				var jValue = jCurObjDiv.find(".cl-q-timer .cl-q-timer-value-number");
				var iTimerLeft = CLZ.oStore[sQuestionId].t - CLZ.oStore[sQuestionId].e;
				switch(argobj.action)
				{
					case "init":
					{
						jCurObjDiv.find(".cl-q-timer").show();
						var jValueContainer = jCurObjDiv.find(".cl-q-timer .cl-q-timer-value");
						jValueContainer.css({ "width": jValueContainer.innerWidth + "px" });
						jValue.html(iTimerLeft);
						var sTW = Math.round(100 * iTimerLeft / CLZ.oStore[sQuestionId].t) + "%";
						jIndicator.width(sTW).show();
						if(oCurObj.data.sMode!="browse")
						{
							sTW = Math.round(100 * (iTimerLeft-1) / CLZ.oStore[sQuestionId].t) + "%";
							jIndicator.animate({ "width": sTW }, { duration: 1000, easing: "linear" } );
							oCurObj.data.iQTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Timer" }); }, 1000);
						}
						break;
					}
					case "stop":
					{
						jIndicator.stop(true, false);
						clearTimeout(oCurObj.data.iQTimer);
						oCurObj.data.iQTimer = null;
						break;
					}
					case "suspend":
					{
						break;
					}
					case "count":
					default:
					{
						if(jCurObjDiv.is(":visible"))
						{
							iTimerLeft--;
							CLZ.oStore[sQuestionId].e++;
							jValue.html(iTimerLeft);
							if(iTimerLeft>0)
							{
								var sTW = Math.round(100 * (iTimerLeft-1) / CLZ.oStore[sQuestionId].t) + "%";
								jIndicator.animate({ "width": sTW }, { duration: 1000, easing: "linear" });
								if(iTimerLeft < 0.25*CLZ.oStore[sQuestionId].t)
								{
									if(iTimerLeft < 0.125*CLZ.oStore[sQuestionId].t)
									{
										jIndicator.removeClass("cl-q-timer-indicator-warn").addClass("cl-q-timer-indicator-panic");
									}
									else
									{
										jIndicator.removeClass("cl-q-timer-indicator-idle").addClass("cl-q-timer-indicator-warn");
									}
								}
								oCurObj.data.iQTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Timer" }); }, 1000);
							}
							else
							{
								jIndicator.width(0).hide();
								oCurObj.data.bTimeout = true;
								oCurObj.FireEvent({ sName: "onTimeout" });
								oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { sReason: "timeout" } });
							}
						}
						break;
					}
				}
				return true;
			}

function CloseAttempt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				var iAttemptIdx = -1;
				for(var i=0; i<CLZ.oStore[sQuestionId].A.length; i++)
				{
					if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
					iAttemptIdx = i;
					break;
				}
				var iNow = (new Date()).valueOf();
				var iStart = CLZ.oStore[sQuestionId].A[iAttemptIdx].s;
				CLZ.oStore[sQuestionId].A[iAttemptIdx] =
				{
					R: CLZ.oStore[sQuestionId].R.slice(0),
					s: iStart,
					d: iNow - iStart - CLZ.oStore[sQuestionId].s,
					u: "c"
				}; // current attempt closed
				iAttemptIdx++;
				var iAttemptsLeft = CLZ.oStore[sQuestionId].A.length - iAttemptIdx;
				if(iAttemptsLeft<0) iAttemptsLeft = 0;
				if(argobj.bCorrect)
				{
					CLZ.oStore[sQuestionId].r = argobj.bCorrect ? "c" : "i";
				}
				jCurObjDiv.find(".cl-q-attempts-num").html( iAttemptsLeft );
				if(iAttemptsLeft==0 || CLZ.oStore[sQuestionId].A[iAttemptIdx]==null)
				{
					oCurObj.data.bNoMoreAttempts = true;
					CLO[argobj.pid].FireEvent({ sName: "onAttemptsLimit" });
				}
				else
				{
					if(argobj.bInitNext)
					{
						CLZ.oStore[sQuestionId].A[iAttemptIdx] =
						{
							s: iNow - CLZ.oStore[sQuestionId].s,
							d: 0,
							u: "i"
						}
						if((CLZ.oStore[sQuestionId].A.length>1) && (iAttemptIdx==CLZ.oStore[sQuestionId].A.length-1))
						{
							oCurObj.data.bLastAttempt = true;
							oCurObj.FireEvent({ sName: "onLastAttemptStart" });
						}
					}
				}
				return true;
			}

function CloseQuestion(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var sQuestionId = argobj.pid;
				oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { action: "stop" } });
				if(argobj.bFail==true)
				{
					oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { bFail: true } });
				}
				var jVars;
				switch(oCurObj.data.sType)
				{
					case "true-false":
					case "choice":
					case "select":
					{
						jVars = jCurObjDiv.find(".cl-var");
						break;
					}
					case "order":
					{
						jVars = jCurObjDiv.find(".cl-var");
						if(jVars.hasClass("ui-draggable"))
						{
							jVars.draggable("destroy");
						}
						break;
					}
					case "numeric":
					case "fib":
					{
						jVars = jCurObjDiv.find(".cl-var");
						jVars.find(".cl-var-input").prop("disabled", true);
						break;
					}
					case "oto":
					case "otm":
					case "mtm":
					{
						jVars = jCurObjDiv.find(".cl-var-bullet");
						if(jVars.hasClass("ui-draggable"))
						{
							jVars.draggable("destroy");
						}
						if(jVars.hasClass("ui-droppable"))
						{
							jVars.droppable("destroy");
						}
						break;
					}
				}
				jVars.addClass("cl-var-disabled");
				jCurObjDiv.find(".cl-btn").remove();		
				if(oCurObj.data.bModal)
				{
					CL.Modal({ bOn: false, sSrcId: argobj.pid });
				}
				CLZ.oStore[sQuestionId].d = (new Date()).valueOf() - CLZ.oStore[sQuestionId].s;
				CLZ.oStore[sQuestionId].u = "c";
				oCurObj.FireEvent({ sName: "onQuestionEnd" })
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return "";
				if(CLO[argobj.pid]==null) return "";
				switch(argobj.property.toLowerCase())
				{
					case "questionmode":
					{
						return ((CLO[argobj.pid].data.sMode=="browse") ? "review" : "normal");
					}
					case "questiontype":
					{
						return CLO[argobj.pid].data.sType;
					}
					case "questionduration":
					{
						return ((CLO[argobj.pid].data.bTimer==true) ? CLZ.oStore[argobj.pid].t : "");
					}
					case "questiontimer":
					{
						return ((CLO[argobj.pid].data.bTimer==true) ? (CLZ.oStore[argobj.pid].t - CLZ.oStore[argobj.pid].e) : "");
					}
					case "itemquantity":
					{
						return CLO[argobj.pid].data.jxVars.length;
					}
					case "questionvalue":
					{
						return CLZ.oStore[argobj.pid].R.join(",");
					}
					case "attemptslast":
					{
						var iAttemptsLeft = 0;
						for(var i=0; i<CLZ.oStore[argobj.pid].A.length; i++)
						{
							if(CLZ.oStore[sQuestionId].A[i].u=="c") continue;
							iAttemptsLeft = CLZ.oStore[argobj.pid].A.length - i;
							break;
						}
						return iAttemptsLeft;
					}
					case "attemptstotal":
					{
						return CLZ.oStore[argobj.pid].A.length;
					}
				}
				return "";
			}
